import { useState } from "react";
import { Box, TextField, Typography,Button } from "@mui/material";
import axios from "axios";

const CallRecordForm=()=>{
    const [callType, setCallType] = useState('');
    const [duration, setDuration] = useState('');
    const [error, setError] = useState('')

    const handleSubmit = async(e)=>{
        e.preventDefault();
        if(!callType||!duration){
            setError("Please fill all fields")
            
        }
        try {
           await axios.post('http:/localhost:8080/call-records',{callType,duration})
            setCallType('');
            setDuration('');
            setError('');
        }catch(err){
            setError('Failed to save call record');
            console.log(err);
        }
    };
    return (
        <Box sx={{ p: 2, maxWidth: 400, margin: '0 auto' }}>
            <Typography variant="h6">Add Call Record</Typography>
            {error && <Typography color="error">{error}</Typography>}
            <form onSubmit={handleSubmit}>
                <TextField label="Call Type" value={callType} onChange={(e)=>setCallType(e.target.value)} fullWidth margin="normal" />
                <TextField
                    label="Duration (in seconds)"
                    type="number"
                    value={duration}
                    onChange={(e) => setDuration(e.target.value)}
                    fullWidth
                    margin="normal"
                />
                <Button type="submit" variant="contained" color="primary" fullWidth>
                    Enter
                </Button>
            </form>
        </Box>
    )
}
export default CallRecordForm;
